// Import React and useState
import React, { useState } from "react";
import "./Dashboard.css"; // Assuming this is the filename where you place your CSS


// Main Dashboard component
function Dashboard() {
  // State for toggling the sidebar, set initial state to false
  const [open, setOpen] = useState(false);

  // Toggle function
  const handleToggle = () => {
    setOpen(!open);
  };

  return (
    <div className={`d-flex ${open ? "" : "closed"}`} id="wrapper">
      <div className={`bg-light border-right ${open ? "" : "closed"}`} id="sidebar-wrapper">
        <div className={`list-group list-group-flush ${open ? "" : "d-none"}`}>
          <a href="/" className="list-group-item list-group-item-action">
          <svg xmlns="http://www.w3.org/2000/svg" width="16" height="16" fill="currentColor" class="bi bi-speedometer" viewBox="0 0 16 16">
  <path d="M8 2a.5.5 0 0 1 .5.5V4a.5.5 0 0 1-1 0V2.5A.5.5 0 0 1 8 2zM3.732 3.732a.5.5 0 0 1 .707 0l.915.914a.5.5 0 1 1-.708.708l-.914-.915a.5.5 0 0 1 0-.707zM2 8a.5.5 0 0 1 .5-.5h1.586a.5.5 0 0 1 0 1H2.5A.5.5 0 0 1 2 8zm9.5 0a.5.5 0 0 1 .5-.5h1.5a.5.5 0 0 1 0 1H12a.5.5 0 0 1-.5-.5zm.754-4.246a.389.389 0 0 0-.527-.02L7.547 7.31A.91.91 0 1 0 8.85 8.569l3.434-4.297a.389.389 0 0 0-.029-.518z"/>
  <path fill-rule="evenodd" d="M6.664 15.889A8 8 0 1 1 9.336.11a8 8 0 0 1-2.672 15.78zm-4.665-4.283A11.945 11.945 0 0 1 8 10c2.186 0 4.236.585 6.001 1.606a7 7 0 1 0-12.002 0z"/>
</svg>Dashboard
          </a>
          <a href="/" className="list-group-item list-group-item-action">
           Performance
          </a>
          <a href="/" className="list-group-item list-group-item-action">
          Sales
          </a>
          {/* Add more menu items with icons as needed */}
        </div>
      </div>
      <div id="page-content-wrapper">
        <button onClick={handleToggle} className={`btn btn-light btn-sm toggle-button ${open ? "open" : ""}`}>
          <span className="icon-bar"></span>
          <span className="icon-bar"></span>
          <span className="icon-bar"></span>
        </button>
      </div>
    </div>
  );
}

export default Dashboard;
