import React, { useState } from "react";

function Item({ icon, name }) {
  return (
    <div className="item">
      {icon}
      <p>{name}</p>
    </div>
  );
}

function Dashboard() {
  const [open, setOpen] = useState(true);

  const handleToggle = () => {
    setOpen(!open);
  };

  const sideContainerVariants = {
    true: {
      width: "15rem",
    },
    false: {
      transition: {
        delay: 0.6,
      },
    },
  };

  const sidebarVariants = {
    true: {},
    false: {
      width: "3rem",
      transition: {
        delay: 0.4,
      },
    },
  };

  const profileVariants = {
    true: {
      alignSelf: "center",
      width: "4rem",
    },
    false: {
      alignSelf: "flex-start",
      marginTop: "2rem",
      width: "3rem",
    },
  };

  return (
    <div className="App">
      <div
        data-Open={open}
        style={sideContainerVariants[open]}
        className="sidebar_container"
      >
        <div
          className="sidebar"
          style={sidebarVariants[open]}
        >
          <div
            onClick={handleToggle}
            className="lines_icon"
          >
            {/* You can replace this with your custom lines icon */}
            Lines Icon
          </div>
          <div
            layout
            style={profileVariants[open]}
            className="profile"
          >
            {/* Replace this with your profile image */}
            <img
              src="https://ae01.alicdn.com/kf/H5be6a0fa5f584a8a8420da2a7d4bc809r/RBRARE-Polaroid-Men-s-Goggle-Driving-Sunglasses-Men-Classic-Low-Profile-Sun-Glasses-For-Men-High.jpg"
              alt="profile_img"
            />
          </div>
          <div className="groups">
            <div className="group">
              <h3
                style={{
                  opacity: open ? 1 : 0,
                  height: open ? "auto" : 0,
                }}
              >
                ANALYTICS
              </h3>
              <Item icon="YourDashboardIcon" name="Dashboard" />
              <Item icon="YourPerformanceIcon" name="Performance" />
            </div>
          </div>
          <div className="group">
            <h3
              style={{
                opacity: open ? 1 : 0,
                height: open ? "auto" : 0,
              }}
            >
              Content
            </h3>
            <Item icon="YourSalesIcon" name="Sales" />
            <Item icon="YourChecklistIcon" name="Checklist" />
            <Item icon="YourCustomersIcon" name="Customers" />
          </div>
          <div className="group">
            <h3
              style={{
                opacity: open ? 1 : 0,
                height: open ? "auto" : 0,
              }}
            >
              CUSTOMIZATION
            </h3>
            <Item icon="YourSegmentsIcon" name="Segments" />
            <Item icon="YourThemesIcon" name="Themes" />
          </div>
        </div>
      </div>
    </div>
  );
}

export default Dashboard;
