import React, { useEffect, useRef, useState } from "react";
import { toast } from "react-toastify";

const ClientRoom = ({ socket }) => {
  const imgRef = useRef(null);
  const [imageURL, setImageURL] = useState("");

  useEffect(() => {
    socket.on("canvasImage", (data) => {
      if (data && data.imageURL) {
        setImageURL(data.imageURL);
      } else {
        // Display an error only when imageURL is not present or valid
        toast.error("Invalid image data received");
      }
    });

    return () => {
      socket.off("canvasImage");
    };
  }, [socket]);

  return (
    <div className="container-fluid">
      <div className="col-md-8 overflow-hidden border border-dark px-0 mx-auto mt-3" style={{ height: "500px" }}>
        <img className="w-100 h-100" ref={imgRef} src={imageURL} alt="canvas" />
      </div>
    </div>
  );
};

export default ClientRoom;
