import React, { useEffect } from "react";
import "./Whiteboard.css";
import { useHistory } from 'react-router-dom';


const WhiteboardRoom = ({ userNo, socket, setUsers, setUserNo }) => {
  // Define placeholder event handlers for canvas interactions
  const handleMouseDown = (event) => {
    // Add logic for mouse down on canvas
  };

  const handleMouseMove = (event) => {
    // Add logic for mouse move on canvas
  };

  const handleMouseUp = (event) => {
    // Add logic for mouse up on canvas
  };
  const handleRefresh = () => {
    alert('Sure you want to refresh?');
    window.location.href = window.location.href; // Refreshes the page by changing the URL
  };
  

  useEffect(() => {
    socket.on("message", (data) => {
      alert(data.message);
    });
  }, [socket]);

  useEffect(() => {
    socket.on("users", (data) => {
      setUsers(data);
      setUserNo(data.length);
    });
  }, [socket, setUsers, setUserNo]);

  return (
    <div className="container-fluid">
      <div className="row">
        <div className="col">
          <h1 className="display-5 pt-4 pb-3 text-center">
            Whiteboard -{userNo}
          </h1>
        </div>
      </div>
      <div className="row">
        <div className="col-lg-10">
          <canvas
            id="canvas"
            width={window.innerWidth}
            height={window.innerHeight}
            onMouseDown={handleMouseDown}
            onMouseMove={handleMouseMove}
            onMouseUp={handleMouseUp}
            style={{ position: "absolute", zIndex: 1 }}
          >
            Canvas
          </canvas>
        </div>
        <div className="col-lg-2">
          <div className="container">
            <div className="col " >
              <button className="btn btn-secondary" onClick={handleRefresh}>
                <svg xmlns="http://www.w3.org/2000/svg" width="16" height="16" fill="currentColor" className="bi bi-people-fill" viewBox="0 0 16 16">
                  <path d="M7 14s-1 0-1-1 1-4 5-4 5 3 5 4-1 1-1 1H7Zm4-6a3 3 0 1 0 0-6 3 3 0 0 0 0 6Zm-5.784 6A2.238 2.238 0 0 1 5 13c0-1.355.68-2.75 1.936-3.72A6.325 6.325 0 0 0 5 9c-4 0-5 3-5 4s1 1 1 1h4.216ZM4.5 8a2.5 2.5 0 1 0 0-5 2.5 2.5 0 0 0 0 5Z" />
                </svg>
              </button>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default WhiteboardRoom;
