import React, { useState, useEffect } from "react";
import axios from "axios"; // You need to have axios or another HTTP library installed

const Dashboard = () => {
  const [profile, setProfile] = useState(null);

  useEffect(() => {
    const fetchUserProfile = async () => {
      try {
        const token = localStorage.getItem("token");
        const decodedToken = parseJwt(token); // Parse the JWT to get user ID
        const userId = decodedToken ? decodedToken._id : null;

        if (userId) {
          const response = await axios.get(`/api/profile/${userId}`, {
            headers: {
              Authorization: `Bearer ${token}`,
            },
          });
          setProfile(response.data.profile);
        }
      } catch (error) {
        console.error("Error fetching user profile:", error);
        // Handle error (e.g., redirect to login page)
      }
    };

    fetchUserProfile();
  }, []); // Empty dependency array means this effect runs once when the component mounts

  const handleLogout = () => {
    localStorage.removeItem("token");
    window.location.reload();
  };

  const parseJwt = (token) => {
    try {
      return JSON.parse(atob(token.split(".")[1]));
    } catch (e) {
      return null;
    }
  };

  return (
    <div className="container-fluid" style={{ position: "relative" }}>
      <div className="btns fix-top right" style={{ position: "absolute", top: 0, right: 0 }}>
        <button className="btn btn-danger" onClick={handleLogout}>
          Logout
        </button>
      </div>
      {profile && (
        <div>
          <h2>Welcome, {profile.firstName}!</h2>
          <p>Email: {profile.email}</p>
          {/* Display other profile information as needed */}
        </div>
      )}
    </div>
  );
};

export default Dashboard;
