// Import React and useState
import React, { useState } from "react";
import styles from "./styles.module.css"; // Import your styles module

// Functional component for each item in the sidebar
function Item({ icon, name }) {
  return (
    <div className={styles.item}>
      {icon}
      <p>{name}</p>
    </div>
  );
}

// Main Dashboard component
function Dashboard() {
  // State for toggling the sidebar
  const [open, setOpen] = useState(true);

  // Toggle function
  const handleToggle = () => {
    setOpen(!open);
  };

  // JSX structure with dynamic styles based on the 'open' state
  return (
    <div className={styles.App}>
      <div className={`${styles.sidebar_container} ${open ? styles.open : styles.closed}`}>
        <div className={`${styles.sidebar} ${open ? styles.open : styles.closed}`}>
          <div onClick={handleToggle} className={styles.toggleButton}>
            {/* You can replace this with your custom lines icon */}
            Toggle
          </div>
          <div className={styles.profile}>
            {/* Replace this with your profile image */}
            <img
              src="https://your-profile-image-url.jpg"
              alt="profile_img"
            />
          </div>
          <div className={styles.groups}>
            <div className={styles.group}>
              <h3>
                ANALYTICS
              </h3>
              <Item icon="YourDashboardIcon" name="Dashboard" />
              <Item icon="YourPerformanceIcon" name="Performance" />
            </div>
          </div>
          <div className={styles.group}>
            <h3>
              Content
            </h3>
            <Item icon="YourSalesIcon" name="Sales" />
            <Item icon="YourChecklistIcon" name="Checklist" />
            <Item icon="YourCustomersIcon" name="Customers" />
          </div>
          <div className={styles.group}>
            <h3>
              CUSTOMIZATION
            </h3>
            <Item icon="YourSegmentsIcon" name="Segments" />
            <Item icon="YourThemesIcon" name="Themes" />
          </div>
        </div>
      </div>
      {/* Rest of your component content */}
    </div>
  );
}

export default Dashboard;
