import React, { useEffect } from "react";


const WhiteboardRoom = ({ userNo, socket, setUsers, setUserNo }) => {
  // Define placeholder event handlers for canvas interactions
  const handleMouseDown = (event) => {
    // Add logic for mouse down on canvas
  };

  const handleMouseMove = (event) => {
    // Add logic for mouse move on canvas
  };

  const handleMouseUp = (event) => {
    // Add logic for mouse up on canvas
  };

  useEffect(() => {
    socket.on("message", (data) => {
      alert(data.message);
    });
  }, [socket]);

  useEffect(() => {
    socket.on("users", (data) => {
      setUsers(data);
      setUserNo(data.length);
    });
  }, [socket, setUsers, setUserNo]);

  return (
    <div className="container-fluid">
      <div className="row">
        <div className="col">
          <h1 className="display-5 pt-4 pb-3 text-center">
            Whiteboard -{userNo}
          </h1>
        </div>
      </div>
      <div className="row">
        <div className="col-lg-10">
          <canvas
            id="canvas"
            width={window.innerWidth}
            height={window.innerHeight}
            onMouseDown={handleMouseDown}
            onMouseMove={handleMouseMove}
            onMouseUp={handleMouseUp}
            style={{ position: "absolute", zIndex: 1 }}
          >
            Canvas
          </canvas>
        </div>
        <div className="col-lg-2">
          <div className="container">
            <div className="col" >
    
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default WhiteboardRoom;
