import React, { useState, useEffect } from "react";
import axios from "axios";

const Dashboard = () => {
  const [userProfile, setUserProfile] = useState({
    firstName: "",
    lastName: "",
    email: "",
  });

  const handleLogout = () => {
    localStorage.removeItem("token");
    window.location.reload();
  };

  useEffect(() => {
    const fetchUserProfile = async () => {
      try {
        const token = localStorage.getItem("token");
        const response = await axios.get("/api/profile", {
          headers: {
            Authorization: `Bearer ${token}`,
          },
        });

        const { firstName, lastName, email } = response.data.profile;
        setUserProfile({ firstName, lastName, email });
      } catch (error) {
        console.error("Error fetching user profile:", error);
        // Handle error, such as redirecting to login page
      }
    };

    fetchUserProfile();
  }, []); // The empty dependency array ensures this effect runs only once on mount

  return (
    <div className="container-fluid" style={{ position: "relative" }}>
      <div className="btns fix-top right" style={{ position: "absolute", top: 0, right: 0 }}>
        <button className="btn btn-danger" onClick={handleLogout}>
          Logout
        </button>
      </div>

      <div>
        <h1>Welcome, {userProfile.firstName}!</h1>
        <p>Email: {userProfile.email}</p>
        {/* Add additional profile information here */}
      </div>
    </div>
  );
};

export default Dashboard;
