// Import React and useState
import React, { useState } from "react";


// Main Dashboard component
function Dashboard() {
  // State for toggling the sidebar
  const [open, setOpen] = useState(true);

  // Toggle function
  const handleToggle = () => {
    setOpen(!open);
  };

  return (
    <div className="d-flex" id="wrapper">
      {/* Sidebar */}
      <div className={`bg-light border-right ${open ? "" : "closed"}`} id="sidebar-wrapper">

        <div className={`list-group list-group-flush ${open ? "" : "d-none"}`}>
          <a href="#" className="list-group-item list-group-item-action">Dashboard</a>
          <a href="#" className="list-group-item list-group-item-action">Performance</a>
          <a href="#" className="list-group-item list-group-item-action">Sales</a>
          <a href="#" className="list-group-item list-group-item-action">Checklist</a>
          <a href="#" className="list-group-item list-group-item-action">Customers</a>
          <a href="#" className="list-group-item list-group-item-action">Segments</a>
          <a href="#" className="list-group-item list-group-item-action">Themes</a>
        </div>
      </div>
      <div id="page-content-wrapper">
        <button onClick={handleToggle} className="btn btn-light btn-sm toggle-button">
          {open ? "Hide Sidebar" : "Show Sidebar"}
        </button>
      </div>
    </div>
  );
}

export default Dashboard;
