import React, { useEffect, useRef, useState } from "react";
import "./Whiteboard.css";

const WhiteboardRoom = ({ socket }) => {
  const canvasRef = useRef(null);
  const [isDrawing, setIsDrawing] = useState(false);
  const [prevX, setPrevX] = useState(0);
  const [prevY, setPrevY] = useState(0);
  const [userNo, setUserNo] = useState(0);
  const [users, setUsers] = useState([]);

  useEffect(() => {
    const canvas = canvasRef.current;
    const context = canvas.getContext("2d");

    const handleMouseDown = (event) => {
      setIsDrawing(true);
      setPrevX(event.clientX - canvas.offsetLeft);
      setPrevY(event.clientY - canvas.offsetTop);
    };

    const handleMouseMove = (event) => {
      if (isDrawing) {
        const x = event.clientX - canvas.offsetLeft;
        const y = event.clientY - canvas.offsetTop;

        context.strokeStyle = "#000"; // Set stroke color
        context.lineWidth = 2; // Set line width
        context.lineCap = "round"; // Set line cap style

        context.beginPath();
        context.moveTo(prevX, prevY);
        context.lineTo(x, y);
        context.stroke();

        setPrevX(x);
        setPrevY(y);
      }
    };

    const handleMouseUp = () => {
      setIsDrawing(false);
    };

    canvas.addEventListener("mousedown", handleMouseDown);
    canvas.addEventListener("mousemove", handleMouseMove);
    canvas.addEventListener("mouseup", handleMouseUp);

    return () => {
      canvas.removeEventListener("mousedown", handleMouseDown);
      canvas.removeEventListener("mousemove", handleMouseMove);
      canvas.removeEventListener("mouseup", handleMouseUp);
    };
  }, [isDrawing, prevX, prevY]);

  useEffect(() => {
    socket.on("message", (data) => {
      alert(data.message);
    });
  }, [socket]);

  useEffect(() => {
    socket.on("users", (data) => {
      setUsers(data);
      setUserNo(data.length);
    });
  }, [socket]);

  return (
    <div className="container-fluid">
      <div className="row">
        <div className="col">
          <h1 className="display-5 pt-4 pb-3 text-center">
            Whiteboard - {userNo}
          </h1>
        </div>
      </div>
      <div className="row">
        <div className="col-lg-10">
          <canvas
            ref={canvasRef}
            id="canvas"
            width={window.innerWidth}
            height={window.innerHeight}
            style={{ position: "absolute", zIndex: 1, border: "1px solid black" }}
          >
            Canvas
          </canvas>
        </div>
      </div>
    </div>
  );
};

export default WhiteboardRoom;
