// ClientRoom.jsx
import React, { useState, useEffect, useRef } from "react";

const ClientRoom = ({ socket }) => {
  const [userNo, setUserNo] = useState(0);
  const imgRef = useRef(null);

  useEffect(() => {
    const handleCanvasImage = (data) => {
      // Assuming data is a base64 encoded image URL
      imgRef.current.src = data;
    };

    socket.on("canvasImage", handleCanvasImage);

    return () => {
      socket.off("canvasImage", handleCanvasImage);
    };
  }, [socket]);

  useEffect(() => {
    socket.on("users", (data) => {
      setUserNo(data.length);
    });

    return () => {
      socket.off("users");
    };
  }, [socket]);

  return (
    <div className="container-fluid">
      <div className="row pb-2">
        <h1 className="display-5 pt-4 pb-3 text-center">
          React Drawing App - Users Online: {userNo}
        </h1>
      </div>
      <div className="row mt-5">
        <div
          className="col-md-8 overflow-hidden border border-dark px-0 mx-auto mt-3"
          style={{ height: "500px" }}
        >
          <img className="w-100 h-100" ref={imgRef} src="" alt="Canvas Image" />
          {/* Other components related to drawing canvas can be added here */}
        </div>
      </div>
    </div>
  );
};

export default ClientRoom;
