import React, { useState } from 'react';
import Carousel from 'react-multi-carousel';
import 'react-multi-carousel/lib/styles.css';
import { Link } from 'react-router-dom';
import { FaTrash } from 'react-icons/fa';

const Home = () => {
  const [show, setShow] = useState(false);
  const responsive = {
    superLargeDesktop: {
      breakpoint: { max: 4000, min: 3000 },
      items: 5
    },
    desktop: {
      breakpoint: { max: 3000, min: 1024 },
      items: 4
    },
    tablet: {
      breakpoint: { max: 1024, min: 464 },
      items: 3
    },
    mdtablet: {
      breakpoint: { max: 992, min: 464 },
      items: 3
    },
    mobile: {
      breakpoint: { max: 464, min: 0 },
      items: 4
    }
  };

  return (
    <div className='pt-5'>
      <div className='w-100 d-flex justify-content-center align-items-center h-250 bg-gradient-to-r from-primary to-secondary position-relative rounded overflow-hidden'>
        <button onClick={() => setShow(!show)} className='px-4 py-2 fs-4 text-center bg-third text-white rounded-3 font-medium hover-bg-fourth position-absolute top-3 end-3'>
          Custom size
        </button>
        <div className={`position-absolute top-16 end-3 gap-3 bg-dark w-250 p-4 text-white ${show ? 'visible opacity-100' : 'invisible opacity-50'} transition-all duration-500`}>
          <div className='d-grid grid-cols-2 pb-4 gap-3'>
            <div className='d-flex gap-2 justify-content-center align-items-start flex-column'>
              <label htmlFor='width'>Width</label>
              <input type='number' name='width' className='w-100 outline-none px-2 py-2 bg-dark-secondary border border-dark rounded' id='width' />
            </div>
            <div className='d-flex gap-2 justify-content-center align-items-start flex-column'>
              <label htmlFor='height'>Height</label>
              <input type='number' name='height' className='w-100 outline-none px-2 py-2 bg-dark-secondary border border-dark rounded' id='height' />
            </div>
          </div>
          <button className='px-4 py-2 fs-3 text-center bg-third text-white rounded-3 font-medium hover-bg-fourth w-100'>
            Create new design
          </button>
        </div>
        <div>
          <h2 className='fs-5 pb-3 pt-6 font-semibold text-white'>What will you design today?</h2>
        </div>
      </div>
      <div>
        <h2 className='fs-4 py-4 font-semibold text-white'>Your recent designs</h2>
        <div>
          <Carousel
            autoPlay={true}
            infinite={true}
            responsive={responsive}
            transitionDuration={500}
          >
            {[1, 2, 3, 4, 5, 6, 7, 8].map((d, i) => (
              <div className='position-relative w-100 h-150 px-2' key={i}>
                <Link className='w-100 h-100 block bg-dark-tertiary p-3 rounded' to='/your-link-path'>
                  <img className='w-100 h-100 rounded overflow-hidden' src="http://localhost:5173/project.png" alt="" />
                </Link>
                <div className='position-absolute invisible cursor-pointer top-1 end-2 text-danger p-2 transition-all duration-500'>
                  <FaTrash />
                </div>
              </div>
            ))}
          </Carousel>
        </div>
      </div>
    </div>
  );
};

export default Home;
