// Import React and useState
import React, { useState } from "react";
import "./Dashboard.css"; // Assuming this is the filename where you place your CSS

// Main Dashboard component
function Dashboard() {
  // State for toggling the sidebar
  const [open, setOpen] = useState(true);

  // Toggle function
  const handleToggle = () => {
    setOpen(!clo);
  };

  return (
    <div className={`d-flex ${open ? "" : "closed"}`} id="wrapper">
      <div className={`bg-light border-right ${open ? "" : "closed"}`} id="sidebar-wrapper">
        <div className={`list-group list-group-flush ${open ? "" : "d-none"}`}>
          <a href="#" className="list-group-item list-group-item-action">Dashboard</a>
          <a href="#" className="list-group-item list-group-item-action">Performance</a>
          {/* Add more menu items as needed */}
        </div>
      </div>
      <div id="page-content-wrapper">
        <button onClick={handleToggle} className={`btn btn-light btn-sm toggle-button ${open ? "" : "open"}`}>
          <span className="icon-bar"></span>
          <span className="icon-bar"></span>
          <span className="icon-bar"></span>
        </button>
      </div>
    </div>
  );
}

export default Dashboard;
