import React, { useEffect, useState } from "react";

const WhiteboardRoom = ({ userNo, socket, setUsers, setUserNo }) => {
  // Define placeholder event handlers for canvas interactions
  const handleMouseDown = (event) => {
    // Add logic for mouse down on canvas
  };

  const handleMouseMove = (event) => {
    // Add logic for mouse move on canvas
  };

  const handleMouseUp = (event) => {
    // Add logic for mouse up on canvas
  };

  useEffect(() => {
    socket.on("message", (data) => {
      alert(data.message);
    });
  }, [socket]);

  useEffect(() => {
    socket.on("users", (data) => {
      setUsers(data);
      setUserNo(data.length);
    });
  }, [socket, setUsers, setUserNo]);

  const [showTools, setShowTools] = useState(true); // Adjust initial state based on your requirement

  return (
    <div className="container-fluid">
      <div className="row">
        <div className="col">
          <h1 className="display-5 pt-4 pb-3 text-center">
            Whiteboard -{userNo}
          </h1>
        </div>
      </div>
      <div className="row">
        <div className="col-lg-10">
          <canvas
            id="canvas"
            width={window.innerWidth}
            height={window.innerHeight}
            onMouseDown={handleMouseDown}
            onMouseMove={handleMouseMove}
            onMouseUp={handleMouseUp}
            style={{ position: "absolute", zIndex: 1 }}
          >
            Canvas
          </canvas>
        </div>
        <div className={`col-lg-2 p-0 overflow-hidden ${showTools ? "" : "collapse"}`}>
          <div className="bg-light" style={{ height: "100vh" }}>
            <h5 className="p-3">Canvas Tools</h5>
            {/* Add canvas tools here */}
            <ul className="list-unstyled">
              <li>Tool 1</li>
              <li>Tool 2</li>
              <li>Tool 3</li>
              {/* Add more tools */}
            </ul>
          </div>
        </div>
      </div>
    </div>
  );
};

export default WhiteboardRoom;
